import React, { useRef, useState } from 'react';
import './App.css';
import UserList from './UserList';
import CreateUser from './CreateUser';

function App() {
  const [inputs, setInputs] = useState({
    username: '',
    email: '',
  });

  const { username, email } = inputs;
  const onChange = e => {
    const { name, value } = e.target;
    setInputs({
      ...inputs,
      [name]: value
    });
  };

  const [users, setUsers] = useState([
    {
        id: 1,
        username: 'bokjiho',
        email: 'bok.jiho@gmail.com',
    },
    {
        id: 2,
        username: 'jiminbok',
        email: 'jiminbok@gmail.com',
    },
    {
        id: 3,
        username: 'ockmilee',
        email: 'leeockmi121@gmail.com',
    },
  ]);

  const nextId = useRef(4);
  const onCreate = () => {
    const user = {
      id: nextId.current,
      username,
      email
    };
    setUsers([...users, user]);

    setInputs({
      username: '',
      email: '',
    });

    nextId.current += 1;
  };

  const onRemove = id => {
    // user.id가 파라미터로 일치하지 않는 원소만 추출해서 새로운 배열을 만듦
    // = user.id가 id인 것을 제거함
    setUsers(users.filter(user => user.id !== id));
  };

  return(
    <>
     <CreateUser 
        username={username}
        email={email}
        onChange={onChange}
        onCreate={onCreate} 
     />
    <UserList users={users} />
    </>
  );
}


