import React from 'react';
import Hello from './Hello';
import './App.css';

function App() {
  const name = 'react';
  const style = {
    backgroundColor: 'black',
    color: 'aqua',
    fontSize: 24, // 기본 단위 px
    padding: '1rem', // 다른 단위 사용 시 문자열로 설정
  }
  return (
    <>
    {/* 우와앙 주석이다 */}
    /* 주석이 없으면 화면에 나타남 */
      <Hello name="react"
        // 오잉 이렇게도 주석이 써진다. 열리는 태그 내부에서는.
      />
      <div style={style}>{name}</div>
      <div className="gray-box"></div>
    </>
  );
}

export default App;
